# -*- coding: utf-8 -*-from django.db import models# Create your models here.class Studexam(models.Model):	'''Studexam Model'''	class Meta(object):		verbose_name= u"Іспити"		verbose_name_plural=u"Іспити"	subject = models.CharField(		max_length=256,		blank= False,		verbose_name=u"Назва предмету")	exam_date = models.CharField(		max_length=256,		blank= False,		verbose_name=u"Дата проведення")	tutor =models.CharField(		max_length=256,		blank=False,		verbose_name= u"Прізвище викладача")	group_exam = models.CharField(		max_length=256,		blank= False,		default ='',		verbose_name=u"Група")	notes = models.TextField(		blank=True,		verbose_name=u"Нотатки")		def __unicode__(self):		return u"%s %s %s %s" % (self.subject, self.tutor, self.exam_date,self.group_exam)